class Modal {constructor(element) {if (!(element instanceof HTMLElement)) {throw new Error("Invalid element provided. Must be an instance of HTMLElement.");}this.element = element;this.backdrop = this.createBackdrop();}createBackdrop() {let backdrop = document.querySelector('#modalBackdrop');if (!backdrop) {backdrop = document.createElement('div');backdrop.id = 'modalBackdrop';backdrop.className = 'modal-backdrop';document.body.insertBefore(backdrop, document.body.firstChild);}backdrop.addEventListener('click', () => this.close());return backdrop;}open() {this.element.querySelector('.modal-content').scrollTop = 0;document.body.classList.add('modal-open');this.element.style.display = "block";this.backdrop.style.display = "block";setTimeout(() => {this.element.classList.add('modal-open', 'show');this.backdrop.classList.add('show');}, 10);}close() {document.body.classList.remove('modal-open');this.element.classList.remove('show');this.backdrop.classList.remove('show');setTimeout(() => {this.element.style.display = "none";this.backdrop.style.display = "none";}, 300);}static bindCloseButton(modalId, buttonSelector) {const modal = Modal.init(modalId);const buttonElement = document.querySelector(`#${modalId} ${buttonSelector}`);if (!buttonElement) {throw new Error(`No button found with Selector : #${modalId} ${buttonSelector}`);}buttonElement.addEventListener('click', () => modal.close());}static init(id) {const element = document.getElementById(id);if (!element) {throw new Error(`No element found with ID: ${id}`);}const modalInstance = new Modal(element);const dismissButtons = element.querySelectorAll('[data-dismiss="modal"]');dismissButtons.forEach(button => {button.addEventListener('click', () => modalInstance.close());});return modalInstance;}}const loadOpenBtn = () => {document.addEventListener('click', (e) => {const btn = e.target.closest('.modal-open-btn');if (btn) {const modal = btn.dataset.modal;Modal.init(modal).open();}});};document.addEventListener('DOMContentLoaded', () => {loadOpenBtn();});