class Validate {static setKeyUp(inputs) {inputs?.forEach(input => {input.addEventListener('keyup', () => {this._validate(input);});});};static Form(inputs) {let is_valid = true;for (const input of inputs || []) {is_valid = this._validate(input);if (!is_valid) {console.table([{ "invalid id": input.id, "invalid name": input.name}]);return is_valid;break;}}return is_valid;};static validateInputPassword(input) {const passwordPattern = /^(?=.*[A-Z])(?=.*\d)[A-Za-z\d@$!%*?&]{6,}$/;return passwordPattern.test(input.value);};static validateInputEmail(input) {const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/;return emailPattern.test(input.value);};static validateInputText(input) {const textPattern = /^(?!\s{1,})[A-Za-zñÑ\s]{3,}$/;return textPattern.test(input.value);};static validateInputTel(input) {const telPattern = /^(\+\d{1,3})?\s?\d?\s?\d{2,4}[\s.-]?\d{3,4}[\s.-]?\d{4}$/;return telPattern.test(input.value);};static _validate(input) {let isValid = false;let tooltip = input.nextElementSibling;switch (input.type) {case 'text':isValid = this.validateInputText(input);break;case 'email':isValid = this.validateInputEmail(input);break;case 'password':isValid = this.validateInputPassword(input);break;case 'tel':isValid = this.validateInputTel(input);break;case 'checkbox':isValid = true;break;case 'hidden':isValid = true;break;}if (isValid) {tooltip.classList.add('hidden');input.style.borderColor = 'hsl(166,77.8%,44.8%)';input.style.borderWidth = '2px';} else {tooltip.classList.remove('hidden');input.style.borderColor = 'hsl(30, 100%, 50%)';input.style.borderWidth = '2px';}return isValid;};};